mapper.select:
SELECT l.LOCATION_ID, l.LOCATION_NAME, l.LOCATION_PLACE, LOCATION_OPENTIME, LOCATION_CLOSETIME, c.SMALL_CATEGORY, li.LOCATION_IMAGE_PATH, l.LOCATION_LATITUDE, l.LOCATION_LONGITUDE, l.LOCATION_REVIEW_COUNT, li.LOCATION_IMAGE_ORIGINALNAME FROM location l INNER JOIN location_image li ON l.LOCATION_ID = li.LOCATION_ID INNER JOIN category c ON l.CATEGORY_ID = c.CATEGORY_ID WHERE l.LOCATION_PLACE LIKE CONCAT('%',#{addrName},'%') AND c.LARGE_CATEGORY = #{largeCate}
AND c.MIDDLE_CATEGORY = #{middleCate}
AND l.LOCATION_NAME LIKE CONCAT('%',#{locationName},'%')
ORDER BY l.LOCATION_REVIEW_COUNT DESC LIMIT #{pageSize} OFFSET #{recordIndex}

SELECT count(*) FROM location l INNER JOIN location_image li ON l.LOCATION_ID = li.LOCATION_ID INNER JOIN category c ON l.CATEGORY_ID = c.CATEGORY_ID WHERE l.LOCATION_PLACE LIKE CONCAT('%',#{addrName},'%') AND c.LARGE_CATEGORY = #{largeCate}
AND c.MIDDLE_CATEGORY = #{middleCate}
AND l.LOCATION_NAME LIKE CONCAT('%',#{locationName},'%')

SELECT l.*, li.* FROM location l LEFT JOIN location_image li ON l.LOCATION_ID = li.LOCATION_ID WHERE l.LOCATION_ID IN
#{locationId}
ORDER BY l.LOCATION_ID DESC
